const styles = theme => ({
  root: {
    verticalAlign: 'bottom',
    minWidth: '100px',    
    marginBottom: '12px',
    marginTop: '12px'
  },

    dropdownStyle: {
      "& ul": {
          backgroundColor: "#000",
      },
      "& li": {
          fontSize: 12,
      },
  },
});

export default styles;
