import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import {
  FormControl,
  Select,
  OutlinedInput
} from '@material-ui/core';
import StyledLabel from '../label';
import styles from './styles';

function StyledSelect(props) {
  const {
    classes,
    label,
    id,
    value,
    handleChange,
    fullWidth,
    options,
    disabled,
    allowNull
  } = props;

  return (
    <FormControl className={classes.root} fullWidth={fullWidth} variant="outlined">
      <StyledLabel label={label} />
      <Select
        native
        open={true}
        value={value}
        onChange={handleChange}
        disabled={disabled}
        fullWidth
        input={
          <OutlinedInput
            name="age"
            id={id}
            labelWidth={0}
          />
        }
      >
        {allowNull && <option value={null}>select</option>}
        {
          options ? options.map((option) => {
            return <option style={{background: option.value == value ? '#fff' : '#000'}} key={option.value} value={option.value}>{option.description}</option>;
          }) : null
        }
      </Select>
    </FormControl>
  );
}

StyledSelect.propTypes = {
  classes: PropTypes.object.isRequired,
  label: PropTypes.string,
  id: PropTypes.string,
  value: PropTypes.string,
  handleChange: PropTypes.func,
  fullWidth: PropTypes.bool,
  disabled: PropTypes.bool,
  allowNull: PropTypes.bool,
};

export default withStyles(styles)(StyledSelect);
